window.onscroll=function(){OnScrollFn()};var header=document.getElementById("pg-header"),sticky=header.offsetTop;function OnScrollFn(){window.pageYOffset>sticky?header.classList.add("sticky"):header.classList.remove("sticky")}function openSidebar(){let e=document.getElementById("sidebar");e.style.display&&"none"!=e.style.display?e.style.display="none":e.style.display="block"}function openSubMenu(e){let t=e.nextElementSibling,l=e.children.item(1);e.classList.toggle("active"),t.classList.toggle("visible"),l.classList.toggle("fa-angle-right"),l.classList.toggle("fa-angle-down")}document.addEventListener("DOMContentLoaded",(()=>{hljs.highlightAll(),setTimeout((()=>{let e=document.getElementsByClassName("hasSub");for(let t=0;t<e.length;t++)"active"==e.item(t).id&&e.item(0).onclick()}),500)}));